import React , {useState} from 'react';
import './index.css';
import { FaHeart,MdDelete } from "react-icons/fa";






const App = () => {
    const [inputdata, setinputdata] = useState("");
    const [list_item, setlist_item] = useState([]);

    const additem =()=>{
      if(!inputdata)
       {
         alert("plzz add item");
         }
       else{
          setlist_item([...list_item, inputdata]);
          setinputdata("");
          }
}
  return (
      <>
      <div className='main-div'>
        <div className='center-div'>
          <h1>  ToDo List</h1>
          <div> <FaHeart/></div>
          <div><MdDelete/></div>
          
          <input type="text" placeholder="add a item" onChange={(event)=>setinputdata(event.target.value)} value={inputdata}/>
          <button onClick={additem}>+</button>
          <i className="fa-solid fa-circle-trash"></i>
          <div>
            {
            list_item.map((curElem,index)=>{
              return ( 
                <li>{curElem}</li>)


            })}
            <div><i class="fa fa-facebook-square"></i></div>

             
            
            
            
          
          </div>
        </div>

      </div>
    
    </>
  );
};

export default App;
